$(function () {
    $(".menu-icon").click(function () {
        $(this).toggleClass("close");
        $("#top-nav").fadeToggle();
    });
    $("#top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });
});
$(window).on('load resize', function () {
    var w = $(window).width();
    var x = 833;
    $("#top-nav").hide();
    $('.menu-icon ').removeClass('close');

});
