$(function () {
    $(".menu-icon").click(function () {
        $(this).toggleClass("close");
        $("#top-nav").fadeToggle();
    });
    
    $("#top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });
     $('.schedule').click(function () {
        var schedule = $('#schedule').offset().top;
        $('html, body').animate({
            scrollTop: schedule
        });
    });
    $('.show').click(function () {
        var show = $('#show').offset().top;
        $('html, body').animate({
            scrollTop: show
        });
    });
});
$(window).on('load resize', function () {
    var w = $(window).width();
    var x = 833;
    $("#top-nav").hide();
    $('.menu-icon ').removeClass('close');

});
